mapper.update:
UPDATE POST_INFO_TB
SET POST_TITLE = #{title}, POST_CONTENT = #{content}
WHERE POST_INDEX = #{postIndex}
    AND POST_AUTHOR_INDEX = #{userIndex}
    AND BOARD_INDEX = #{boardIndex};

mapper.delete:
DELETE FROM POST_INFO_TB
WHERE POST_INDEX = #{postIndex}
    AND POST_AUTHOR_INDEX = #{userIndex};
