SET COMMENT_UPVOTES = COMMENT_UPVOTES + 1
, COMMENT_DOWNVOTES = COMMENT_DOWNVOTES - 1
WHERE COMMENT_INDEX = #{commentIndex}
    AND POST_INDEX = #{postIndex};

mapper.insert:
