mapper.select:
SELECT POST_INDEX
FROM POST_INFO_TB
WHERE

        BOARD_INDEX = #{boardIndex}
        AND 

    POST_INDEX > #{postIndex}
ORDER BY POST_INDEX ASC
LIMIT 1;

SELECT POST_INDEX
FROM POST_INFO_TB
WHERE

        BOARD_INDEX = #{boardIndex}
        AND 

    POST_INDEX < #{postIndex}
ORDER BY POST_INDEX DESC
LIMIT 1;
