mapper.select:
SELECT PAYMENT_INDEX, phv.USER_INDEX, ITEM_NAME,PAYMENT_DATE, PAYMENT_METHOD, PAYMENT_AMOUNT, PAYMENT_STATUS,SOURCE_TABLE FROM PAYMENT_HISTORY_VW phv ,USER_INFO_TB ui WHERE phv.USER_INDEX = ui.USER_INDEX and USER_ID = #{userID}
AND ( ITEM_NAME LIKE CONCAT('%', #{searchKeyword} , '%') OR PAYMENT_METHOD LIKE CONCAT('%', #{searchKeyword} , '%') OR PAYMENT_STATUS LIKE CONCAT('%', #{searchKeyword} , '%') OR PAYMENT_AMOUNT LIKE CONCAT('%', #{searchKeyword} , '%') )
AND SOURCE_TABLE = 'game'
AND SOURCE_TABLE = 'avatar'
AND SOURCE_TABLE = 'cash'
ORDER BY PAYMENT_DATE DESC LIMIT #{startNum}, #{endNum};

SELECT COUNT(*) FROM PAYMENT_HISTORY_VW phv ,USER_INFO_TB ui WHERE phv.USER_INDEX = ui.USER_INDEX and USER_ID = #{userID}
AND SOURCE_TABLE = 'game'
AND SOURCE_TABLE = 'avatar'
AND SOURCE_TABLE = 'cash'
AND ( ITEM_NAME LIKE CONCAT('%', #{searchKeyword} , '%') OR PAYMENT_METHOD LIKE CONCAT('%', #{searchKeyword} , '%') OR PAYMENT_STATUS LIKE CONCAT('%', #{searchKeyword} , '%') OR PAYMENT_AMOUNT LIKE CONCAT('%', #{searchKeyword} , '%') );
;
